    },

    {
      name: "Mr John Villoth",
      desg: " ",
      college: "Modern College of Business and Science",
      chair: "",
    },
  ];

  let wd = [
    {
      name: "Dr Cecil Donald A",
      desg: "Department of Computer Science",
      college: "CHRIST (Deemed to be University)",
      chair: "Chair",
    },
    {
      name: "Joseph K Iype",
      desg: "Department of Computer Science",
